              {/* daqui pra baixo é da forma normal sem exportar o elemento filho pra outro arquivo */}
              {/* <input
                type="text"
                name="email"
                value={this.state.email}
                onChange={this.handleChange}
              /> */}
              {/* <input
                type="checkbox"
                name="vaiComparecer"
                value={this.state.vaiComparecer}
                onChange={this.handleChange}
              /> */}
              {/* <input
                type="file"
                name="file"
                value={this.state.file}
                onChange={this.handleChange}
              /> */}
            </fieldset>
          </form>
        </div>
      </div>
    );
  }
}

export default Form;
