import TodoContext from '../context/TodoContext';

function TodoList() {
  const value = this.context;
  const { todos } = value;

  return (
    <div>
      <ul>
        {todos.map((todo, i) => <li key={i}>{todo}</li>)}
      </ul>
    </div>
  );
}

TodoList.contextType = TodoContext;

export default TodoList;
